mapper.select:
SELECT fied.Id, fied.head_line, fied.user_qq, fied.user_wx, fied.phone, fied.link, fied.createDate, fied.company_name, fied.procode, fied.citycode, fied.countycode, fied.details, fied.address, fied.type, fied.fw, fied.service, fied.msgCode, SUBSTRING(file.path,33) as path, fied.price, file.type as f_type FROM fied INNER JOIN file ON fied.msgCode = file.msgCode WHERE 1 = 1
AND fied.type = #{type}
AND file.type = 'G' GROUP BY fied.msgCode ORDER BY fied.createDate DESC

SELECT fied.Id, fied.head_line, fied.user_qq, fied.user_wx, fied.phone, fied.link, fied.createDate, fied.company_name, fied.procode, fied.citycode, fied.countycode, fied.details, fied.address, fied.type, fied.fw, fied.service, fied.userId, SUBSTRING(file.path,33) as path, fied.price, file.type as f_type FROM fied INNER JOIN file ON fied.userId = file.userId WHERE 1 = 1
AND fied.id = #{id}
AND file.type = 'G' GROUP BY fied.msgCode

SELECT fied.Id, fied.head_line, fied.user_qq, fied.user_wx, fied.phone, fied.link, fied.createDate, fied.company_name, fied.procode, fied.citycode, fied.countycode, fied.details, fied.address, fied.type, fied.fw, fied.service, fied.userId, SUBSTRING(file.path,33) as path, fied.price, file.type as f_type FROM fied INNER JOIN file ON fied.userId = file.userId WHERE 1 = 1
AND fied.procode = #{procode}
AND fied.citycode = #{citycode}
AND fied.countycode = #{countycode}
AND fied.type = #{type}
AND fied.service like "%"#{fw}"%"
AND file.type = 'G' GROUP BY fied.msgCode
